import { StatusBar } from "../node_modules/expo-status-bar";
import React, { useState } from "react";
import {
  Text,
  View,
  Image,
  TextInput,
  TouchableOpacity,
} from "react-native";
import styles from '../style/LoginStyle'
 
export default function Login({navigation}) {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const logado = () => {
    navigation.reset({
        index: 0,
        routes: [{name: "Index"}]
    })
  }

  const userRegister = () => {
    navigation.navigate("UserRegister")
  }
 
 
  return (
    <View style={styles.container}>
      <Image style={styles.image} source={require("../assets/carrinhoCompra.png")} />
 
      <StatusBar style="auto" />
      <View style={styles.inputView}>
        <TextInput
          style={styles.TextInput}
          placeholder="Email"
          textAlign="center"
          placeholderTextColor="#003f5c"
          onChangeText={(email) => setEmail(email)}
        />
      </View>
 
      <View style={styles.inputView}>
        <TextInput
          style={styles.TextInput}
          placeholder="Senha"
          placeholderTextColor="#003f5c"
          textAlign="center"
          secureTextEntry={true}
          onChangeText={(password) => setPassword(password)}
        />
      </View>
 
      <TouchableOpacity>
        <Text style={styles.forgot_button}>Esqueceu a senha?</Text>
      </TouchableOpacity>
 
      <TouchableOpacity style={styles.signUpBtn} onPress={() => userRegister()}>
        <Text style={styles.loginText}>Inscreva-se</Text>
      </TouchableOpacity>

      <TouchableOpacity style={styles.loginBtn} onPress={() => logado()}> 
        <Text style={styles.loginText}>Entrar</Text>
      </TouchableOpacity>
    </View>
  );
}
